mapper.select:
select
  id, name, parent_id, type, menu_url, status, is_show, icon, permission_key, created_by, created_time, update_by, update_time, is_deleted
from auth_permission


        and id = #{id}


        and name = #{name}


        and parent_id = #{parentId}


        and type = #{type}


        and menu_url = #{menuUrl}


        and status = #{status}


        and isShow = #{show}


        and icon = #{icon}


        and permission_key = #{permissionKey}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

select count(1)
from auth_permission


        and id = #{id}


        and name = #{name}


        and parent_id = #{parentId}


        and type = #{type}


        and menu_url = #{menuUrl}


        and status = #{status}


        and isShow = #{show}


        and icon = #{icon}


        and permission_key = #{permissionKey}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}

select * from auth_permission
where id in

    #{id}
